import { useState } from 'react';

function PageTitle() {
    return (
        <h2>Add/Edit</h2>
    );
}

function NewDeckArea() {
    return (
        <>
            <h3>Deck</h3>
            <label htmlFor='new-deck-name'>New Deck Name</label>
            <input type='text'></input><br />
            <input type='button' value={"Add Deck"}></input>
        </>
    );
}

function NewChapterArea() {
    return (
        <>
            <h3>Chapter</h3>
            <label htmlFor='deck-select'>Select Deck</label>
            <select name='deck-select'>
                <option value={0}>Deck 1</option>
            </select><br />
            <label htmlFor='new-chapter-name'>New Chapter Name</label>
            <input type='text'></input>
            <input type='button' value={"Add Chapter"}></input>
        </>
    );
}

export default function AddEdit() {
    return (
      <>
        <PageTitle />
        <NewDeckArea />
        <NewChapterArea />
      </>
    );  
  }
